INSERT INTO `novel` VALUES (1, '九州缥缈录', '《九州 · 缥缈录》小说是江南所著幻想史诗巨著，小说以虚构“九州”世界为背景，徐徐展开一轴腥风血雨的乱世长卷。当这个世界就要崩溃，当星辰和阳光也熄灭，沉默已久的乱世之轮重新开始运转，乱世诸名将和未来的帝王们整备了盔甲，立起标志着各家徽记的大旗，去向不知结局的战场。', '江南', 'https://www.luoxia.com/images/2017/12/jiuzhoupml.jpg', NULL, 1, 'https://www.luoxia.com/piaomiaolu/');
INSERT INTO `novel` VALUES (2, '基督山伯爵', '《基督山伯爵》（旧译《基督山恩仇录》）讲述了19世纪法国拿破仑“百日王朝”时期，一位名叫爱德蒙·唐泰斯的大副受到陷害后的悲惨遭遇以及日后以基督山伯爵身份成功复仇的故事。', '大仲马', 'https://www.luoxia.com/images/2015/06/jidushan.jpg', NULL, 1, 'https://www.luoxia.com/jidushan/');
INSERT INTO `novel` VALUES (3, '武动乾坤', '《武动乾坤》是天蚕土豆所著的一部东方玄幻小说，讲述了大炎王朝天都郡炎城青阳镇，落魄的林氏子弟林动在山洞间偶然捡到一块神秘的石符而走上的逆袭之路。', '天蚕土豆', 'https://www.luoxia.com/images/2016/09/wudongqk.jpg', NULL, 1, 'https://www.luoxia.com/qiankun/');
INSERT INTO `novel` VALUES (4, '全职高手', '《全职高手》小说，作者蝴蝶蓝，讲述了网游顶尖高手叶修遭到俱乐部驱逐，而后召集一群志同道合的伙伴重返巅峰之路的故事。', '蝴蝶蓝', 'https://www.luoxia.com/images/2017/12/quanzhigos.jpg', NULL, 1, 'https://www.luoxia.com/quanzhigaoshou/');
INSERT INTO `novel` VALUES (5, '斗破苍穹', '天才少年萧炎在创造了空前绝后的修炼纪录后突然成了废人，三年时间，家族冷遇，旁人轻视，未婚妻退婚……种种打击接踵而至。', '天蚕土豆', 'https://www.luoxia.com/images/2014/12/dpcq.jpg', NULL, 1, 'https://www.luoxia.com/doupo/');
INSERT INTO `novel` VALUES (6, '白夜行', '《白夜行》小说讲述了多年以前，大阪一栋废弃建筑中发现一名遭利器刺死男子的故事。案件扑朔迷离，始终悬而未决。此后20年间，案件滋生出的恶逐渐萌芽生长，绽放出恶之花。案件相关者的人生逐渐被越来越重的阴影笼罩……', '东野圭吾', 'https://www.luoxia.com/images/2015/04/baiyexing.jpg', NULL, 1, 'https://www.luoxia.com/baiye/');
